class ReportHTML < ReportModule

	def info
		return 'This plugin produces HTML reports'
	end
	
	def file_extension
		return ".html"
	end

	def format
		return "html"
	end

	def htmlhead
		htmlout = ""
		htmlout << "<HTML><HEAD><TITLE>dockscan Report</TITLE></HEAD></HTML>\n"
		htmlout << "<BODY>\n"
		htmlout << '<img width="220" height="169" title="" alt="" src="data:image/png;base64," />'
		return htmlout
	end

	def htmlfoot
		htmlout = ""
		htmlout << "</BODY>\n"
		htmlout << "</HTML>\n"
		return htmlout
	end

	def report(opts)
		output=""
		output << htmlhead
		output << "<h2>dockscan Report<h2>"
		issues = sortvulns
		7.downto(4) do |sev|
			if issues.key?(sev)
				output << "<h3>" << sev2word(sev) << " vulnerabilities</h3>\n"
				issues[sev].each do |v|
					output << "<TABLE>\n"
					output << "<TR>\n"
					output << "<TD COLSPAN=2>" << v.vuln.title << "</TD>\n"
					output << "<TD>" << sev2word(v.vuln.severity) << "</TD>\n"
					output << "</TR>\n"
					output << "<TR><TD COLSPAN=3>" << v.vuln.description << "</TD></TR>\n"
					output << "<TR><TD COLSPAN=3>" << v.output << "</TD></TR>\n"
					output << "<TR><TD COLSPAN=3>" << v.vuln.solution << "</TD></TR>\n"
					output << "<TR><TD>No CVSS</TD><TD>ClassName</TD><TD>Else</TD></TR>\n"
					output << "</TABLE>\n\n"
				end
				output << "\n"
			end
		end
		return output

	end
end

